import React, {useState} from 'react';
import './App.css';

function App() {
	const [name , setName] = useState('');
	const [age , setAge] = useState('');
	const [email , setEmail] = useState('');
	const [address , setAddress] = useState('');

	const handleChange =(e)=>{
	setName(e.target.value);
	}

	const handleAgeChange =(e)=>{
	setAge(e.target.value);
	}

	const handleEmailChange =(e)=>{
	setEmail(e.target.value);
	}

  const handleAddressChange =(e)=>{
    setAddress(e.target.value);
  }

	const handleSubmit=(e)=>{
	if(name==='' || age==='' || email==='' || address==='')
	{

		alert('Please fill out this field');
	}
	else{
		alert('Name :"' + name +
		'" ,Age :"'+age +'" and Email :"' + email + '",Address :"'+address +'"');
	}
	e.preventDefault();

	}
return (
	<div className="App" style={ {padding: 30}}>
	<header className="App-header">
	<form onSubmit={(e) => {handleSubmit(e)}}>
	<h3> Information Form </h3>

		<label >
		Name:
		</label><br/>
		<input type="text" value={name} required onChange={(e) => {handleChange(e)}} /><br/>

		<label >
		Age:
		</label><br/>
		<input type="number" value={age} required onChange={(e) => {handleAgeChange(e)}} /><br/>

		<label>
		Email:
		</label><br/>
		<input type="email" value={email} required onChange={(e) => {handleEmailChange(e)}} /><br/>

    <label>
		Address:
    <br/>
      <select style={{marginTop: 5, marginBottom: 5}} type='address' value={address} onChange={(e) => {handleAddressChange(e)}}>
            <option >Cam Ranh</option>
            <option >Cam Lam</option>
            <option >Nha Trang</option>
            <option >Dien Khanh</option>
      </select>
		</label><br/>

		<input type="submit" value="Submit"/>
	</form>
	</header>
	</div>
);
}

export default App
